mapper.update:
UPDATE
       TBL_MEMBER
   SET
       NICKNAME=#{nickname}
    ,  GENDER=#{gender}
    ,  AGE=#{age}
 WHERE
       MEMBER_ID=#{memberId}

UPDATE
       TBL_MEMBER
   SET
       PASSWORD=#{newPassword}
 WHERE
       MEMBER_ID=#{memberId}

mapper.insert:
INSERT INTO
       TBL_MEMBER_EXPERIENCE(
       MEMBER_ID
    ,  COMPANY_NAME
    ,  DESCRIPTION
    ,  START_DATE
    ,  END_DATE
)
VALUES (
       #{memberId}
    ,  #{companyName}
    ,  #{description}
    ,  #{startDate}
    ,  #{endDate}
)
